import React, {useEffect, useState} from 'react';
import {useHistory, useParams, withRouter} from "react-router";
import axios from "axios";
import moment from "moment";
import Button from "antd-button-color";
import {Form, Modal, Popover} from "antd";
import {QRCode} from "react-qrcode-logo";
import Barcode from 'react-barcode';


const ProjectDetails = () => {

    const {id} = useParams();
    const history = useHistory();

    const [form] = Form.useForm();
    const [state, setState] = useState(null);
    const [qrModal, setQrModal] = useState({mode: false, type: 'qr-code'});
    const [employees, setEmployees] = useState([]);
    useEffect(() => {

        axios.create().get('/api/projects/ref/' + id).then(ft => {

            setState(ft.data);
            form.setFieldsValue({
                ...ft.data,
                fiche: ft.intervention?.ref,
                dateNotif: moment(ft.data.dateNotif),
                dateDebut: moment(ft.data.dateDebut ? ft.data.dateDebut : moment())
            });
        })

        axios.create().get('/api/employees/names').then(ft => {
            setEmployees(ft.data);
        })

    }, [id, form]);


    const findPilote = (pilote) => {
        const obj = employees.find(x => x.name === pilote);
        return obj?.firstName + " " + obj?.lastName;
    };

    const qrCodeModal = <Modal footer={[]} visible={qrModal.mode}
                               onOk={() => setQrModal(f => ({...f, mode: false}))}
                               onCancel={() => setQrModal(f => ({...f, mode: false}))}>
        <div id="general-code" className="row justify-content-center">
            {
                qrModal.type === "qr-code" ? <QRCode
                        logoImage={"data:image/png;base64,"}
                        value={window.location.href}/> :
                    <Barcode value={id}/>
            }
        </div>
        <div className="row justify-content-center mt-5">
            <Button type="primary" onClick={() => {

                const mywindow = window.open('', 'PRINT', 'height=400,width=600');

                mywindow.document.write(document.getElementById('general-code').innerHTML);

                mywindow.document.close();
                mywindow.focus();

                mywindow.print();
                mywindow.close();
            }}>Imprimer</Button>
        </div>
    </Modal>;

    return (
        <div className="container">
            <div className="row flex-column mt-5">
                <div className="col">
                    <table className="table table-bordered table-hover table-striped">
                        <tbody>
                        <tr>
                            <th>Référence</th>
                            <td>{state?.ref}</td>
                        </tr>
                        <tr>
                            <th>Projet</th>
                            <td>{state?.name}</td>
                        </tr>
                        <tr>
                            <th>Maitre d'ouvrage</th>
                            <td>{state?.maitreDouvrage || "-"}</td>
                        </tr>
                        <tr>
                            <th>Etat</th>
                            <td>{state?.etat}</td>
                        </tr>
                        <tr>
                            <th>Fiche d'intervention</th>
                            <td>{state?.intervention?.ref}</td>
                        </tr>
                        <tr>
                            <th>Date de Notification</th>
                            <td>{moment(state?.dateNotif).format('DD/MM/YYYY')}</td>
                        </tr>
                        <tr>
                            <th>Date debut</th>
                            {
                                state?.dateDebut ? <td>{moment(state?.dateDebut).format('DD/MM/YYYY')}</td> : <td>-</td>
                            }

                        </tr>
                        <tr>
                            <th>Architecte</th>
                            <td>{state?.architecte || "-"}</td>
                        </tr>
                        <tr>
                            <th>N° Marche</th>
                            <td>{state?.nemMarche || "-"}</td>
                        </tr>
                        <tr>
                            <th>Client</th>
                            <td>{state?.client || "-"}</td>
                        </tr>
                        <tr>
                            <th>Bureau de contrôle</th>
                            <td>{state?.control || "-"}</td>
                        </tr>
                        {
                            state?.piloteMetreur ? <tr>
                                <th>Responsable Metreur</th>
                                <td>{findPilote(state?.piloteMetreur)}</td>
                            </tr> : null
                        }
                        {
                            state?.piloteStructure ? <tr>
                                <th>Responsable Structure</th>
                                <td>{findPilote(state?.piloteStructure)}</td>
                            </tr> : null
                        }{
                            state?.piloteTechnique ? <tr>
                                <th>Responsable Technique</th>
                                <td>{findPilote(state?.piloteTechnique)}</td>
                            </tr> : null
                        }{
                            state?.piloteVRD ? <tr>
                                <th>Responsable VRD</th>
                                <td>{findPilote(state?.piloteVRD)}</td>
                            </tr> : null
                        }

                        </tbody>
                    </table>
                </div>
                <div className="col d-flex flex-column align-items-center">

                    <div className="col-xs">
                        <div className="col"><h4 className="text-center">Autre</h4></div>
                        <Button className="m-1" onClick={() => {
                            history.push('/lots/' + state?.id);
                        }} type={"success"}>Planning Général</Button>


                        <Popover trigger="click" content={
                            <div className="row flex-column">
                                <div className="col m-1">
                                    <Button className="w-100" onClick={() => {
                                        history.push('/project-details/' + state?.id + '?lot=' + btoa('Technique'))
                                    }
                                    }>Technique</Button>
                                </div>
                                <div className="col m-1">
                                    <Button className="w-100" onClick={() => {
                                        history.push('/project-details/' + state?.id + '?lot=' + btoa('Structure'))
                                    }
                                    }>Structure</Button>
                                </div>
                                <div className="col m-1">
                                    <Button className="w-100" onClick={() => {
                                        history.push('/project-details/' + state?.id + '?lot=' + btoa('VRD'))
                                    }
                                    }>VRD</Button>
                                </div>
                                <div className="col m-1">
                                    <Button className="w-100" onClick={() => {
                                        history.push('/project-details/' + state?.id + '?lot=' + btoa('Metreur'))
                                    }
                                    }>Metreur</Button>
                                </div>
                            </div>
                        }>
                            <Button className="m-1" type="warning">Planning Detaillé</Button>
                        </Popover>


                        {qrCodeModal}
                        <Popover trigger="click" content={
                            <div className="row flex-column">
                                <div className="col m-1">
                                    <Button onClick={() => {
                                        document.getElementById('code').click();
                                        setQrModal({mode: true, type: 'bar-code'});
                                    }} className="w-100">Bar code</Button>
                                </div>
                                <div className="col m-1">
                                    <Button onClick={() => {
                                        document.getElementById('code').click();
                                        setQrModal({mode: true, type: 'qr-code'});
                                    }} className="w-100">Qr code</Button>
                                </div>
                            </div>
                        }>
                            <Button id="code" className="m-1" type="info">générer un code</Button>
                        </Popover>
                    </div>
                    <div className="col"><h4 className="text-center">Fichiers</h4></div>
                    <div className="col-xs">
                        <Button className="m-1" type={"primary"}>Facture</Button>
                        <Button className="m-1" type={"warning"}>Ordre service</Button>
                        <Button className="m-1" onClick={() => {
                            history.push("/fiche-intervention/" + state?.id)
                        }} type={"success"}>Fiche d’intervention</Button>
                        <Button className="m-1" onClick={() => {
                            history.push('/files/' + state?.id);
                        }} type={"dark"}>Autre fichiers</Button>
                    </div>


                </div>
            </div>
        </div>
    );
};

export default withRouter(ProjectDetails);
